// store.ts
import { createStore, applyMiddleware, compose } from 'redux'
import {MakeStore, createWrapper, Context} from 'next-redux-wrapper';
import {reducer} from "./states/announcement/reducer";
import { connectRouter, routerMiddleware } from 'connected-react-router'

export interface State {
  message: 'No announcement...'
}


const initialState = {}
const enhancers = []
const middleware = [routerMiddleware(history)]
// export const initialRootState = {tick:"ghgh"};


// create your reducer
// const reducer = (state: State = {tick: 'init'}, action: AnyAction) => {
//     switch (action.type) {
//         case HYDRATE:
//             // Attention! This will overwrite client state! Real apps should use proper reconciliation.
//             return {...state, ...action.payload};
//         case 'TICK':
//             return {...state, tick: action.payload};
//         default:
//             return state;
//     }
// };

const composedEnhancers = compose(
  applyMiddleware(...middleware),
  ...enhancers
)

// create a makeStore function
 const makeStore: MakeStore<State> = (context: Context) =>  createStore(
  connectRouter(reducer),initialState,composedEnhancers)

// export an assembled wrapper
export const wrapper = createWrapper<State>(makeStore, {debug: true});
