// store.ts
import { createStore, applyMiddleware } from 'redux'
import {MakeStore, createWrapper, Context} from 'next-redux-wrapper';
import {reducer,initialState} from "./states/announcement/reducer";
import { composeWithDevTools } from 'redux-devtools-extension';

import thunkMiddleware from 'redux-thunk'

export interface State {
  message: 'No announcement...'
}

// export const initialRootState = {tick:"ghgh"};



// create your reducer
// const reducer = (state: State = {tick: 'init'}, action: AnyAction) => {
//     switch (action.type) {
//         case HYDRATE:
//             // Attention! This will overwrite client state! Real apps should use proper reconciliation.
//             return {...state, ...action.payload};
//         case 'TICK':
//             return {...state, tick: action.payload};
//         default:
//             return state;
//     }
// };

// create a makeStore function
const makeStore: MakeStore<State> = (context: Context) => 
createStore(reducer, initialState, composeWithDevTools(applyMiddleware(thunkMiddleware)))


// let store = createStore(quotes, initialState, applyMiddleware(httpMiddleware));  


// export an assembled wrapper
export const wrapper = createWrapper<State>(makeStore, {debug: true});
